# Function that always rounds 5 up
round2 = function(x, n) {
  posneg = sign(x)
  z = abs(x)*10^n
  z = z + 0.5
  z = trunc(z)
  z = z/10^n
  z*posneg
}

# Importance sampling functions (http://www.ejwagenmakers.com/papers.html)
dbetamix = function(x, alpha, beta, w)
{
  # density for a mixture of Beta(1,1) and Beta(alpha, beta)
  dbetamix <- w + (1-w)*dbeta(x,alpha,beta)
  return(dbetamix)
}

rbetamix = function(N, alpha, beta, w)
{
  # draws from density for a mixture of Beta(1,1) and Beta(alpha, beta)
  N.11      <- floor((w*N)+.5)
  rbetamix1 <- c(rbeta(N.11,1,1),rbeta(N-N.11,alpha,beta))
  rbetamix2 <- sample(rbetamix1,N,replace=F)
  return(rbetamix2)
}

fitbeta = function(theta)
{
  # Estimates parameters from beta distribution
  mean.theta <- mean(theta)
  var.theta  <- var(theta)
  alpha      <- mean.theta * ( (mean.theta*(1-mean.theta)/var.theta) - 1 )
  beta       <- (1-mean.theta) * ( (mean.theta*(1-mean.theta)/var.theta) - 1 )
  return(c(alpha,beta))
}

plot.fit = function(theta,alpha,beta,name)
{
  par(cex.main = 1.5, mar = c(5, 6, 4, 5) + 0.1, mgp = c(3.5, 1, 0), cex.lab = 1.5,
      font.lab = 2, cex.axis = 1.3, bty = "n", las=1)
  hist(theta, 100, xlim=c(0,1), ylim = c(0,13), freq=F,
       ylab="Density", xlab = name, main=" ")
  par(new=T)
  plot ( function( x ) dbeta( x, alpha, beta ), 0, 1, ylim = c(0,13), lwd=2, lty=1,
         ylab=" ", xlab = " ", main=" ",col="green")
  par(new=T)
  plot ( function( x ) dbetamix( x, alpha, beta, w=w ), 0, 1, ylim = c(0,13), lwd=2, lty=1,
         ylab=" ", xlab = " ", main=" ", col="red")
}

# Compute probabilites for samples from importance distribution -----------
CategoryProb <- function(samples = importanceSamples, model = modelToUse) {
  # Save samples per parameter so computing category probabilities is easy
  # Don't use i to index loop, because it messes with paramter i
  for (j in 1:length(importanceSamples)) {
    assign(names(importanceSamples)[j], importanceSamples[[j]])
  }
  
  # Empty matrix for category probabilities
  categoryProb <- matrix(nrow = length(importanceSamples[[1]]), ncol = 16)
  
  if (model == "independence") {
    # Category probabilities independence model
    categoryProb[, 1] = (1 - p) * (1 - np) * (1 - q) * (1 - nq)
    categoryProb[, 2] = (1 - p) * (1 - np) * (1 - q) * nq        
    categoryProb[, 3] = (1 - p) * (1 - np) * q * (1 - nq)        
    categoryProb[, 4] = (1 - p) * (1 - np) * q * nq              
    categoryProb[, 5] = (1 - p) * np * (1 - q) * (1 - nq)        
    categoryProb[, 6] = (1 - p) * np * (1 - q) * nq              
    categoryProb[, 7] = (1 - p) * np * q * (1 - nq)              
    categoryProb[, 8] = (1 - p) * np * q * nq                    
    categoryProb[, 9] = p * (1 - np) * (1 - q) * (1 - nq)        
    categoryProb[, 10] = p * (1 - np) * (1 - q) * nq             
    categoryProb[, 11] = p * (1 - np) * q * (1 - nq)             
    categoryProb[, 12] = p * (1 - np) * q * nq                   
    categoryProb[, 13] = p * np * (1 - q) * (1 - nq)             
    categoryProb[, 14] = p * np * (1 - q) * nq                   
    categoryProb[, 15] = p * np * q * (1 - nq)                   
    categoryProb[, 16] = p * np * q * nq
  } else if (model == "inferenceGuessing") {
    # Category probabilities inference guessing model
    categoryProb[, 1] = (1 - a) * (1 - p) * (1 - np) * (1 - q) * (1 - nq)
    categoryProb[, 2] = a * c * (1 - d) * (1 - s) * i + (1 - a) * (1 - p) * (1 - np) * (1 - q) * nq
    categoryProb[, 3] = a * c * (1 - d) * s * i + (1 - a) * (1 - p) * (1 - np) * q * (1 - nq)
    categoryProb[, 4] = a * (1 - c) * (1 - x) * (1 - d) * i + (1 - a) * (1 - p) * (1 - np) * q * nq
    categoryProb[, 5] = a * c * d * (1 - s) * i + (1 - a) * (1 - p) * np * (1 - q) * (1 - nq)
    categoryProb[, 6] = a * (1 - c) * x * (1 - s) * i + (1 - a) * (1 - p) * np * (1 - q) * nq
    categoryProb[, 7] = a * c * d * (1 - s) * (1 - i) + a * c * (1 - d) * s * (1 - i) + (1 - a) * (1 - p) * np * q * (1 - nq)
